CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

CREATE TABLE continentes (
    ID UUID PRIMARY KEY DEFAULT uuid_generate_v4(), 
    codigo SMALLSERIAL UNIQUE, 
    codigo_tipo_ISO VARCHAR(2) UNIQUE, 
    nombre TEXT UNIQUE NOT NULL, 
    observacion TEXT
);

