CREATE TABLE paises (
    ID UUID PRIMARY KEY DEFAULT uuid_generate_v4(), 
    codigo SERIAL UNIQUE, 
    codigo_tipo_ISO VARCHAR(10) UNIQUE, 
    nombre TEXT UNIQUE NOT NULL, 
    observacion TEXT, 
    ID_continente UUID NOT NULL, 
    es_soberano BOOLEAN DEFAULT FALSE

    UNIQUE (nombre, ID_continente), 

    FOREIGN KEY (ID_continente) REFERENCES continentes(ID) ON DELETE CASCADE
);

